mapper.update:
UPDATE
    menu
SET
    sort = menu.sort + 1



            and parent_id IS NULL


            and parent_id = #{parentId}





            and sort >= #{sort}


            and sort > #{sort}

mapper.select:
SELECT ifnull(MAX(sort), 0) AS new_sort FROM menu b


        WHERE b.parent_id IS NULL


        WHERE b.parent_id = #{parentId}
